mapper.update:
          UPDATE post
		  SET post_title = #{postTitle},
		  post_content = #{postContent},
		  post_thumnail = #{postThumnail},
		  user_id = #{userId}
		  category_id = #{categoryId}

mapper.select:
          SELECT c.category_id, c.category_title, p.post_id, p.post_title, p.post_content, p.post_thumnail, p.user_id
		  FROM category c left JOIN post p ON c.user_id = p.user_id
		  WHERE c.user_id = #{userId} AND c.category_id = #{categoryId} AND p.post_id = #{postId}

mapper.update:
         UPDATE post
		  SET post_title =#{postTitle},
		  post_content = #{postContent},
		  post_thumnail = #{postThumnail},
		  category_id = #{categoryId}
		  WHERE post_id = #{postId} AND user_id = #{userId}

         UPDATE post
		  SET post_title =#{postTitle},
		  post_content = #{postContent},
		  post_thumnail = #{noFile},
		  category_id = #{categoryId}
		  WHERE post_id = #{postId} AND user_id = #{userId}

mapper.select:
SELECT p.totalCount,p.totalPage,p.currentPage, (case currentPage when 0 then 1 else 0 END) FIRST, (case currentPage when totalPage-1 then 1 else 0 END) last FROM ( select count(*) totalCount, CEILING(count(*)/5) totalPage, #{page} as currentPage, 0 as first, 0 as last FROM post WHERE user_id=#{userId}
AND post_title like '%${keyword}%'
) p

SELECT p.totalCount,p.totalPage,p.currentPage, (case currentPage when 0 then 1 else 0 END) FIRST, (case currentPage when totalPage-1 then 1 else 0 END) last FROM ( select count(*) totalCount, CEILING(count(*)/5) totalPage, #{page} as currentPage, 0 as first, 0 as last FROM post WHERE user_id=#{userId} AND category_id=#{categoryId}
AND post_title like '%${keyword}%'
) p
