mapper.select:
        SELECT *
		FROM user
		WHERE username=#{username} AND password = #{password}


        SELECT *
		FROM user u
		WHERE u.user_id=#{userId} AND u.password= #{password}

        SELECT username
		FROM user
		WHERE username = #{username}

        SELECT nickname
		FROM user
		WHERE nickname = #{nickname}

        SELECT email
		FROM user
		WHERE email = #{email}

mapper.delete:
DELETE FROM love WHERE user_id = #{userId};
DELETE FROM post WHERE user_id = #{userId};
DELETE FROM category WHERE user_id = #{userId};
DELETE FROM subscribe WHERE user_id = #{userId} AND users_id=#{userId};
DELETE FROM user WHERE user_id = #{userId};
